int class_camera_projection::create_image_canvas(const cv::Mat* image, std::vector<pcl::PointXY>* pts)
{
	pcl::PointXY pix;     

	//Create the canvas
	int c=0; int l=0; 
	for (l=image->rows-1; l>=0; l--) 
	{    
		pix.x = c; 
		pix.y = l; 
		pts->push_back(pix);
	}    

	c=0; l=0; 
	for (c=0; c<image->cols; c++) 
	{    
		pix.x = c; 
		pix.y = l; 
		pts->push_back(pix);
	}    

	c=image->cols-1; l=0; 
	for (l=0; l<image->rows; l++) 
	{    
		pix.x = c; 
		pix.y = l; 
		pts->push_back(pix);
	}    

	c=0; l=image->rows-1;
	for (c=image->cols-1; c>=0; c--) 
	{    
		pix.x = c; 
		pix.y = l; 
		pts->push_back(pix);
	}    

	return 1;}

